        public async Task Send2Excel()
        {
			IEnumerable<EntryDataDetailsEx> lst = null;

            using (var ctx = new EntryDataDetailsExRepository())
            {
                lst = await ctx.GetEntryDataDetailsExesByExpressionNav(vloader.FilterExpression, vloader.NavigationExpression, new List<string>() { "EntryDataEx" }).ConfigureAwait(continueOnCapturedContext: false);
            }
             if (lst == null || !lst.Any()) 
              {
                   MessageBox.Show("No Data to Send to Excel");
                   return;
               }
            var s = new ExportToExcel<EntryDataDetailsExExcelLine, List<EntryDataDetailsExExcelLine>>
            {
                dataToPrint = lst.Select(x => new EntryDataDetailsExExcelLine
                {
 
                    EntryDataId = x.EntryDataId ,

                    EntryDataDate = x.EntryDataEx.InvoiceDate,
                    Type = x.EntryDataEx.Type,
                    
 
                    LineNumber = x.LineNumber ,
                    
 
                    ItemNumber = x.ItemNumber ,
                    
 
                    Quantity = x.Quantity ,
                    
 
                    Units = x.Units ,
                    
 
                    ItemDescription = x.ItemDescription ,
                    
 
                    Cost = x.Cost ,
                    
 
                    QtyAllocated = x.QtyAllocated ,
                    
 
                    UnitWeight = x.UnitWeight ,
                    
 
                    DoNotAllocate = x.DoNotAllocate ,
                    
 
                    TariffCode = x.TariffCode ,
                    
 
                    CNumber = x.CNumber ,
                    
 
                    CLineNumber = x.CLineNumber ,
                    
 
                    Downloaded = x.Downloaded ,
                    
 
                    DutyFreePaid = x.DutyFreePaid ,
                    
 
                    Total = x.Total 
                    
                }).ToList()
            };
            using (var sta = new StaTaskScheduler(numberOfThreads: 1))
            {
                await Task.Factory.StartNew(s.GenerateReport, CancellationToken.None, TaskCreationOptions.None, sta).ConfigureAwait(false);
            }
        }

